// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import {IEAS, AttestationRequest, AttestationRequestData} from "@ethereum-attestation-service/eas-contracts/contracts/IEAS.sol";
import {NO_EXPIRATION_TIME, EMPTY_UID} from "@ethereum-attestation-service/eas-contracts/contracts/Common.sol";
import {CarbonCredits} from "./CarbonCredits.sol"; // Import your ERC1155 contract

contract Attestation {
    error InvalidEAS();

    // The address of the global EAS contract.
    IEAS private immutable _eas;
    CarbonCredits private _erc1155Contract; // Declare an instance of the ERC1155 interface

    constructor(IEAS eas, CarbonCredits erc1155Contract) {
        if (address(eas) == address(0) || address(erc1155Contract) == address(0)) {
            revert InvalidEAS();
        }

        _eas = eas;
        _erc1155Contract = erc1155Contract;
    }

    function attestUint(
        bytes32 schema,
        string memory projectName,
        string memory country,
        string memory vintage,
        string memory standard,
        string memory methodology,
        string memory creditAmount,
        string memory issuerName,
        string memory issuerCountry,
        address recptAddr
    ) external {
        bytes32 attestationId = _eas.attest(
            AttestationRequest({
                schema: schema,
                data: AttestationRequestData({
                    recipient: recptAddr,
                    expirationTime: NO_EXPIRATION_TIME,
                    revocable: true,
                    refUID: EMPTY_UID,
                    data: abi.encode(
                        projectName,
                        country,
                        vintage,
                        standard,
                        methodology,
                        creditAmount,
                        issuerName,
                        issuerCountry
                    ),
                    value: 0
                })
            })
        );

        // // Check if the attestation was successful
        // require(
        //     _eas.attestationState(attestationId) == IEAS.AttestationState.Approved,
        //     "Attestation failed"
        // );

        if (attestationId > 0) {
            // Convert the methodology string to a uint256 tokenId
            uint256 tokenId = uint256(keccak256(abi.encodePacked(methodology)));

            // If attestation successful, call ERC1155 mint function
            _erc1155Contract.mint(recptAddr, tokenId, 1); // Adjust parameters accordingly
        }
    }
}

